var comFuc = {
  verifyMobile(mobile){
      var re = /^1[3|4|5|8|7]\d{9}$/;

      if (re.test(mobile)) {
          return true;
      }
      return false;

  },
    verifyCode(code){
        var re = /^\d{4}$/;
        if(re.test(code)){
            return true;
        }
        return false;
    }
};

module.exports = comFuc


